                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     clear all;

dx = 0.01; x = 0:dx:1; x= x'; NX = length(x);
dt = dx; T = 0.8; t = 0:dt:T; NT = length(t);

u = zeros(NX,NT);
u(:,1) = 1;
u(1,:) = 3; u(end,:) = 2;

D = 0.1;
A = diag((1+2*D*dt/dx^2)*ones(NX,1))-diag(D*dt/dx^2*ones(NX-1,1),1)-diag(D*dt/dx^2*ones(NX-1,1),-1);
A(1,1) = 1; A(1,2) =0; A(end,end-1) = 0; A(end,end)=1;


for n = 1:NT-1
    u(:,n+1) = A\u(:,n);
    u(1,n+1) = 3;
    u(NX,n+1) = 2;
end

%%DMD use psedo inverse
X1 = u(:,1:end-1);
X2 = u(:,2:end);
A_dmd = X2*pinv(X1);
u_dmd = 0*u;
u_dmd(:,1) = u(:,1);
for n = 1:NT-1
    u_dmd(:,n+1) = A_dmd*u_dmd(:,n);
end

%% Res-xDMD
X1_tilde = [X1;ones(1,length(t)-1)];
% X1_tilde(1,:) = 0; X1_tilde(end-1,:) = 0;
Am = (X2-X1)*pinv(X1_tilde);

u_xdmd = 0*u;
u_xdmd(:,1) = u(:,1);

for n = 1:NT-1
    u_xdmd(:,n+1) = u_xdmd(:,n)+Am*[u_xdmd(:,n);1];
end


figure
width = 2.1;     % Width in inches
height = 1.8;    % Height in inches
set(gcf,'InvertHardcopy','on');
set(gcf,'PaperUnits', 'inches');
papersize = get(gcf, 'PaperSize');
left = (papersize(1)- width)/2;
bottom = (papersize(2)- height)/2;
myfiguresize = [left, bottom, width, height];
set(gcf,'PaperPosition', myfiguresize);
hold on;
plot(t,log10(sum((u-u_dmd).^2,1)./sum(u.^2,1)),'k-','LineWidth',1);
plot(t,log10(sum((u-u_xdmd).^2,1)./sum(u.^2,1)),'r-.','LineWidth',1);
xlabel('$t$','FontUnits','points','interpreter','latex',...
    'FontSize',10);
ylabel('log relative error','FontUnits','points','interpreter','latex',...
    'FontSize',10);
legend({'DMD','xDMD'},'FontUnits','points','interpreter','latex',...
    'FontSize',10,'Location','Best');
legend('boxoff') 
title('Representation','FontUnits','points','interpreter','latex',...
    'FontSize',10);





%% extrapolation
u_ex1 = 0*u_dmd;
u_ex3 = 0*u_xdmd;
u_ex = 0*u;
u_ex(:,1) = u(:,end);
u_ex1(:,1) = u_dmd(:,end);
u_ex3(:,1) = u_xdmd(:,end);
for n = 1:NT-1
    u_ex1(:,n+1) = A_dmd*u_ex1(:,n);
    u_ex3(:,n+1) = u_ex3(:,n)+Am*[u_ex3(:,n);1];
    u_ex(:,n+1) = A\u_ex(:,n);
    u_ex(1,n+1) = 3;
    u_ex(NX,n+1) = 2;
end

figure
width = 2.1;     % Width in inches
height = 1.8;    % Height in inches
set(gcf,'InvertHardcopy','on');
set(gcf,'PaperUnits', 'inches');
papersize = get(gcf, 'PaperSize');
left = (papersize(1)- width)/2;
bottom = (papersize(2)- height)/2;
myfiguresize = [left, bottom, width, height];
set(gcf,'PaperPosition', myfiguresize);
hold on;
plot(t+0.8,log10(sum((u_ex-u_ex1).^2,1)./sum(u_ex.^2,1)),'k-','LineWidth',1);
plot(t+0.8,log10(sum((u_ex-u_ex3).^2,1)./sum(u_ex.^2,1)),'r-.','LineWidth',1);
xlabel('$t$','FontUnits','points','interpreter','latex',...
    'FontSize',10);
ylabel('log relative error','FontUnits','points','interpreter','latex',...
    'FontSize',10);
legend({'DMD','xDMD'},'FontUnits','points','interpreter','latex',...
    'FontSize',10,'Location','Best');
legend('boxoff') 
title('Extrapolation','FontUnits','points','interpreter','latex',...
    'FontSize',10);


%% interpolation
%%DMD use psedo inverse
s = 9;
rng(s);
index1 = randperm(79,29);
index1 = sort(index1)+1;
index2 = index1+1;

X1 = [u(:,1),u(:,index1)];
X2 = [u(:,2),u(:,index2)];
A_dmd = X2*pinv(X1);
u_in1 = 0*u;
u_in1(:,1) = u(:,1);

for n = 1:NT-1
    u_in1(:,n+1) = A_dmd*u_in1(:,n);
end


%% mDMD
X1_tilde = [X1;ones(1,length(index1)+1)];
% X1_tilde(1,:) = 0; X1_tilde(end-1,:) = 0;
A_mdmd = (X2-X1)*pinv(X1_tilde);

u_in3 = 0*u;
u_in3(:,1) = u(:,1);

for n = 1:NT-1
    u_in3(:,n+1) = u_in3(:,n)+A_mdmd*[u_in3(:,n);1];
end

figure
width = 2.1;     % Width in inches
height = 1.8;    % Height in inches
set(gcf,'InvertHardcopy','on');
set(gcf,'PaperUnits', 'inches');
papersize = get(gcf, 'PaperSize');
left = (papersize(1)- width)/2;
bottom = (papersize(2)- height)/2;
myfiguresize = [left, bottom, width, height];
set(gcf,'PaperPosition', myfiguresize);
hold on;
plot(t,log10(sum((u-u_in1).^2,1)./sum(u.^2,1)),'k-','LineWidth',1);
plot(t,log10(sum((u-u_in3).^2,1)./sum(u.^2,1)),'r-.','LineWidth',1);
xlabel('$t$','FontUnits','points','interpreter','latex',...
    'FontSize',10);
ylabel('log relative error','FontUnits','points','interpreter','latex',...
    'FontSize',10);
legend({'DMD','xDMD'},'FontUnits','points','interpreter','latex',...
    'FontSize',10,'Location','Best');
legend('boxoff') 
title('Interpolation','FontUnits','points','interpreter','latex',...
    'FontSize',10);
