                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     clear all;

dx = 0.01; x = 0:dx:1; x= x'; NX = length(x);
dt = dx; T = 100; t = 0:dt:T; NT = length(t);

u = zeros(NX,NT);
u(:,1) = 10*exp(-(x-0.5).^2*40);
% u(1,:) = 1;% u(end,:) = 1;

D = 0.1;
A = diag((1+2*D*dt/dx^2)*ones(NX,1))-diag(D*dt/dx^2*ones(NX-1,1),1)-diag(D*dt/dx^2*ones(NX-1,1),-1);
A(1,1) = 1; A(1,2) =0; A(end,end-1) = 0; A(end,end)=1;


for n = 1:NT-1
    u(:,n+1) = A\(u(:,n)+dt*x);
    u(1,n+1) = u(2,n+1);
    u(NX,n+1) = u(NX-1,n+1);
end


M = 81;
%%DMD
X1 = u(:,1:M-1);
X2 = u(:,2:M);
A_dmd1 = X2*pinv(X1);
u_dmd1 = 0*u;
u_dmd1(:,1) = u(:,1);
for n = 1:NT-1
    u_dmd1(:,n+1) = A_dmd1*u_dmd1(:,n);
end

%%DMD-id
X1 = u(:,1:M-1);
X2 = u(:,2:M);
A_dmd2 = (X2-X1)*pinv(X1);
u_dmd2 = 0*u;
u_dmd2(:,1) = u(:,1);
for n = 1:NT-1
    u_dmd2(:,n+1) = u_dmd2(:,n)+A_dmd2*u_dmd2(:,n);
end

%% mDMD
X1_tilde = [X1;ones(1,M-1)];
% X1_tilde(1,:) = 0; X1_tilde(end-1,:) = 0;
Am1 = X2*pinv(X1_tilde);

u_dmd3 = 0*u;
u_dmd3(:,1) = u(:,1);

for n = 1:NT-1
    u_dmd3(:,n+1) = Am1*[u_dmd3(:,n);1];
end

%% mDMD-id
X1_tilde = [X1;ones(1,M-1)];
% X1_tilde(1,:) = 0; X1_tilde(end-1,:) = 0;
Am2 = (X2-X1)*pinv(X1_tilde);

u_dmd4 = 0*u;
u_dmd4(:,1) = u(:,1);

for n = 1:NT-1
    u_dmd4(:,n+1) = u_dmd4(:,n)+Am2*[u_dmd4(:,n);1];
end

figure
width = 3.3;     % Width in inches
height = 2.2;    % Height in inches
set(gcf,'InvertHardcopy','on');
set(gcf,'PaperUnits', 'inches');
papersize = get(gcf, 'PaperSize');
left = (papersize(1)- width)/2;
bottom = (papersize(2)- height)/2;
myfiguresize = [left, bottom, width, height];
set(gcf,'PaperPosition', myfiguresize);
hold on;
plot(t(1:100:end),log10(sum((u(:,1:100:end)-u_dmd1(:,1:100:end)).^2,1)./sum(u(:,1:100:end).^2,1)),'k-','LineWidth',1);
plot(t(1:100:end),log10(sum((u(:,1:100:end)-u_dmd2(:,1:100:end)).^2,1)./sum(u(:,1:100:end).^2,1)),'g.','LineWidth',1);
plot(t(1:100:end),log10(sum((u(:,1:100:end)-u_dmd3(:,1:100:end)).^2,1)./sum(u(:,1:100:end).^2,1)),'b--','LineWidth',1);
plot(t(1:100:end),log10(sum((u(:,1:100:end)-u_dmd4(:,1:100:end)).^2,1)./sum(u(:,1:100:end).^2,1)),'r-.','LineWidth',1);
xlabel('$t$','FontUnits','points','interpreter','latex',...
    'FontSize',10);
ylabel('log relative error','FontUnits','points','interpreter','latex',...
    'FontSize',10);
legend({'DMD','rDMD','gDMD','xDMD'},'FontUnits','points','interpreter','latex',...
    'FontSize',10,'Location','Best');
legend('boxoff') 
title('Long Time Extrapolation Error','FontUnits','points','interpreter','latex',...
    'FontSize',10);



figure
width = 3.3;     % Width in inches
height = 2.2;    % Height in inches
set(gcf,'InvertHardcopy','on');
set(gcf,'PaperUnits', 'inches');
papersize = get(gcf, 'PaperSize');
left = (papersize(1)- width)/2;
bottom = (papersize(2)- height)/2;
myfiguresize = [left, bottom, width, height];
set(gcf,'PaperPosition', myfiguresize);
hold on;
plot(x,u(:,1001),'b-','LineWidth',1);
plot(x(1:5:end),u_dmd1(1:5:end,1001),'bo','Markersize',3);
plot(x(1:5:end),u_dmd4(1:5:end,1001),'bx','Markersize',3);
plot(x,u(:,end),'r-','LineWidth',1);
plot(x(1:5:end),u_dmd1(1:5:end,end),'ro','Markersize',3);
plot(x(1:5:end),u_dmd4(1:5:end,end),'rx','Markersize',3);
%set(gca, 'YScale', 'log')
xlabel('$x$','FontUnits','points','interpreter','latex',...
    'FontSize',10);
ylabel('$u$','FontUnits','points','interpreter','latex',...
    'FontSize',10);
legend({'t = 10 reference','t = 10 DMD','t = 10 xDMD','t = 100 reference','t = 100 DMD','t = 100 xDMD'},...
    'FontUnits','points','interpreter','latex','FontSize',10,'Location','Best');
legend('boxoff') 
title('Long Time Extrapolation','FontUnits','points','interpreter','latex',...
    'FontSize',10);
